CREATE INDEX `deduction_employeeID` ON `deduction` (`employeeID`);
CREATE INDEX `documentrequest_employeeID` ON `documentrequest` (`employeeID`);
CREATE INDEX `documentrequest_documentID` ON `documentrequest` (`documentID`);
CREATE INDEX `information_roleID` ON `information` (`roleID`);
CREATE INDEX `leaveapplication_employeeID` ON `leaveapplication` (`employeeID`);
CREATE INDEX `leaveapplication_leaveID` ON `leaveapplication` (`leaveID`);
CREATE INDEX `notification_senderID` ON `notification` (`senderID`);
CREATE INDEX `overtime_positionID` ON `overtime` (`positionID`);
CREATE INDEX `position_departmentID` ON `position` (`departmentID`);

COMMIT;
PRAGMA ignore_check_constraints = ON;
PRAGMA foreign_keys = ON;
PRAGMA journal_mode = WAL;
PRAGMA synchronous = NORMAL;
